import React,{useState} from 'react';
import logo from './logo.svg';
import './App.css';
import Button from './components/Button';
import Input from "./components/Input";
import Clear from "./components/Clear";

function App() {

  const[input, setInput] = useState("Hey");

  function addInput(val){
     setInput({input: input+val})
  };


  return (
    <div className="App">
      <div className="calculator_wrap">
        <div className = "row">
          <Input input={input}></Input>
        </div>

        <div className = "row">
          <Button> 7</Button>
          <Button> 8</Button>
          <Button> 9</Button>
          <Button> *</Button>
        </div>
        <div className = "row">
          <Button> 4</Button>
          <Button> 5</Button>
          <Button> 6</Button>
          <Button> -</Button>
        </div>
        <div className = "row">
          <Button> 1</Button>
          <Button> 2</Button>
          <Button> 3</Button>
          <Button> +</Button>
        </div>
        <div className = "row">
          <Button> .</Button>
          <Button> 0</Button>
          <Button> =</Button>
          <Button> /</Button>
        </div>
        <div className = "row">
          <Clear handleClear={() => setInput("")}></Clear>
        </div>
      </div>
    </div>
  );
}

export default App;
