import React,{Component, useState} from 'react';
import logo from './logo.svg';
import './App.css';
import Button from './components/Button';
import Input from "./components/Input";
import Clear from "./components/Clear";

class App extends Component{

  constructor(props){
    super(props);

    this.state = {
      input: ""
    };
  }

  addInput = val =>{
   return this.setState({input: this.state.input + val})
  };

  render(){
  return(
  <div className="App">
  <div className="calculator_wrap">
    <div className = "row">
      <Input input={this.state.input}></Input>
    </div>

    <div className = "row">
      <Button buttonHandler={this.addInput}> 7</Button>
      <Button buttonHandler={this.addInput}> 8</Button>
      <Button buttonHandler={this.addInput}> 9</Button>
      <Button buttonHandler={this.addInput}> *</Button>
    </div>
    <div className = "row">
      <Button> 4</Button>
      <Button> 5</Button>
      <Button> 6</Button>
      <Button> -</Button>
    </div>
    <div className = "row">
      <Button> 1</Button>
      <Button> 2</Button>
      <Button> 3</Button>
      <Button> +</Button>
    </div>
    <div className = "row">
        <Button> .</Button>
        <Button> 0</Button>
        <Button> =</Button>
        <Button> /</Button>
      </div>
      <div className = "row">
        <Clear handleClear={() => this.setState("")}></Clear>
      </div>
    </div>
  </div>)
  }

}

export default App;
